if(is.null(input$slide) | is.null(input$radio)){
  plot.new()
}else{
  if( input$radio=='scatter') {
     plot(rnorm(input$slide))
  }else{
     plot(density(rnorm(input$slide)), col='blue', lwd=2)
  }
}

